mapper.insert:
INSERT INTO review (reviewNo, videoId, userId, content, regDate)
VALUES
	(#{reviewNo}, #{videoId}, #{userId}, #{content}, NOW());

mapper.select:
SELECT *
FROM review
WHERE videoId = #{videoId} AND reviewNo = #{reviewNo};

mapper.update:
UPDATE review
SET content = #{content}
WHERE videoId = #{videoId} AND reviewNo = #{reviewNo};
